import React from 'react';
import './Message.css'; // Assuming you have or will create a separate CSS for messages

const Message = ({ message }) => {
  return (
    <div className={`message ${message.sender === 'You' ? 'user-message' : 'bot-message'}`}>
      <div className="message-bubble">
        <p>{message.text}</p>
        <span className="message-sender">{message.sender}</span>
      </div>
    </div>
  );
};

export default Message;
